import { useState, useEffect } from "react";
import { auth, createUserProfileDocument } from "./firebase.utils";
import { onAuthStateChanged } from "firebase/auth";
import { onSnapshot } from "firebase/firestore";

export const useGoogleUser = () => {
  const [currentUser, setCurrentUser] = useState({});

  useEffect(() => {
    const unsubscribeFromAuth = onAuthStateChanged(
      auth,
      async (userAuth) => {
        if (userAuth) {
          const userRef = await createUserProfileDocument(userAuth);
          // Now calls `onSnapshot` as a function; DocumentReference passed as first argument
          // and callback function is now the second argument
          onSnapshot(userRef, (snapshot) => {
            setCurrentUser({
              id: snapshot.id,
              ...snapshot.data(),
            });
          });
          console.log(currentUser);
        }
        // user is not signed in, so userAuth will be null
        else {
          setCurrentUser(null);
        }
      },
      [currentUser.id]
    );

    return () => {
      unsubscribeFromAuth();
    };
  }, [userAuth]);

  return currentUser;
};
