import { useState, useEffect } from "react";
// firebase related imports
import { auth, createUserProfileDocument } from "./firebase.utils";
import { onAuthStateChanged } from "firebase/auth";
import { onSnapshot } from "firebase/firestore";

export const useGoogleUser = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const unsubscribeFromAuth = onAuthStateChanged(auth, async (userAuth) => {
      console.log(userAuth);
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);
        console.log(userRef);
        // Now calls `onSnapshot` as a function; DocumentReference passed as first argument
        // and callback function is now the second argument
        const unsubscribeFromSnapshot = onSnapshot(
          userRef,
          ({ id, ...data }) => {
            setCurrentUser(id);
            setUserData(...data);
          }
        );
        // detach the listener
        unsubscribeFromSnapshot();
      }
      // user is not signed in, so userAuth will be null
      else {
        setCurrentUser(null);
        setUserData(null);
      }
    });

    return () => {
      unsubscribeFromAuth();
    };
  });
  console.log(currentUser);
  console.log(userData);
  return { userData, currentUser };
};
