import React, { useEffect } from "react";
// react routing
import { Route, Routes } from "react-router-dom";
// components
import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from "../collection/collection.component";
// redux
import { useDispatch, useSelector } from "react-redux";
import { selectIsCollectionFetching } from "../../redux/shop/shop.selector";
import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import { createStructuredSelector } from "reselect";

// HOC (high order component)
import WithSpinner from "../../components/with-spinner/with-spinner.component";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const ShopPage = () => {
  const { isCollectionFetching } = useSelector(structuredSelector);
  console.log(isCollectionFetching);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchCollectionsStartAsync());
  }, [dispatch]);

  return (
    <div className="shop-page">
      <Routes>
        <Route
          path="/"
          element={
            <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} />
          }
        />
        <Route
          path=":collectionId"
          element={
            <CollectionPageWithSpinner isLoading={isCollectionFetching} />
          }
        />
      </Routes>
    </div>
  );
};

const structuredSelector = createStructuredSelector({
  selectIsCollectionFetching: selectIsCollectionFetching,
});

export default ShopPage;
